p 'Дан целочисленный массив. Найти минимальный из его локальных минимумов.'
p 'array = [24,19,1,23,45,22,12,19,39,20,51,62,33,44,15,16,17,18,19,100] p (1...array.size-1).select{ |v| (array[v] < array[v-1]) && (array[v] < array[v+1]) }.size' 
array = [24,19,1,23,45,22,12,19,39,20,51,62,33,44,15,16,17,18,19,100]
p (1...array.size-1).select{ |v| (array[v] < array[v-1]) && (array[v] < array[v+1]) }.sort.first